import React, { useState, useEffect } from 'react'
import axios from 'axios'
import styled from 'styled-components'
import { Button } from '@mantine/core';


const Task_li  =styled.li`
list-style:none;
font-size:25px;
diplay:flex;
`;

const TaskName = styled.p`

`;

function TodoList() {
  const [todos, setTodos] = useState([]);

  const getTodos = () => {
    return axios
      .get("http://localhost:3000/todos")
      .then((res) => {
        if (res !== ''){
          setTodos(res.data);
        }
      })
      .catch(() => console.error);
  };

  const toggleComplete = async (id, index) => {
    const complete = todos[index].complete;
    await axios.put(`http://localhost:3000/todos/${id}`, {
      complete: !complete,
    });
    getTodos();
  };

  const deleteTodo = (todoId) => {
    return axios
      .delete(`http://localhost:3000/todos/${todoId}`)
      .then(() => getTodos())
      .catch(console.error());
  };

  useEffect(() => {
    getTodos();
  }, []);



  return (
    <>
      <h1>Todo List</h1>
      <ul>
      {todos.map((todo, index) => (
        <Task_li index={index}>
          <TaskName>{todo.complete ? <s>{todo.name}</s>: todo.name}
          </TaskName>

         <>
         <Button size="xs" type="submit" onClick={() => toggleComplete(todo.id, index)}>
            完了
          </Button>
          <Button size="xs" type="submit" onClick={() => deleteTodo(todo.id)}>
            削除
          </Button>
         </>

        </Task_li>
      ))}
    </ul>

    </>
  )
}

export default TodoList
