import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'
import styled from 'styled-components'
import { ImCheckboxChecked, ImCheckboxUnchecked } from 'react-icons/im'
import { AiFillEdit } from 'react-icons/ai'

const SearchAndButtton = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`

const SearchForm = styled.input`
  font-size: 20px;
  width: 100%;
  height: 40px;
  margin: 10px 0;
  padding: 10px;
`

const RemoveAllButton = styled.button`
  width: 16%;
  height: 40px;
  background: #f54242;
  border: none;
  font-weight: 500;
  margin-left: 10px;
  padding: 5px 10px;
  border-radius: 3px;
  color: #fff;
  cursor: pointer;
`

const TodoName = styled.span`
  font-size: 27px;
  ${({ is_completed }) => is_completed && `
    opacity: 0.4;
  `}
`

const Row = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin: 7px auto;
  padding: 10px;
  font-size: 25px;
`

const CheckedBox = styled.div`
  display: flex;
  align-items: center;
  margin: 0 7px;
  color: green;
  cursor: pointer;
`

const UncheckedBox = styled.div`
  display: flex;
  align-items: center;
  margin: 0 7px;
  cursor: pointer;
`

const EditButton = styled.span`
  display: flex;
  align-items: center;
  margin: 0 7px;
`

function TodoList() {
  const [todos, setTodos] = useState([]);

  const getTodos = () => {
    return axios
      .get("http://localhost:3000/todos")
      .then((res) => {
        if (res !== ''){
          setTodos(res.data);
        }
      })
      .catch(() => console.error);
  };

  const toggleComplete = async (id, index) => {
    const complete = todos[index].complete;
    await axios.put(`http://localhost:3000/todos/${id}`, {
      complete: !complete,
    });
    getTodos();
  };

  const deleteTodo = (todoId) => {
    return axios
      .delete(`http://localhost:3000/todos/${todoId}`)
      .then(() => getTodos())
      .catch(console.error());
  };

  return (
    <>
      <h1>Todo List</h1>
      <ul>
      {todos.map((todo, index) => (
        <li index={index}>
          {todo.complete ? <s>{todo.name}</s> : todo.name}
					<button type="submit" onClick={() => toggleComplete(todo.id, index)}>
            完了
          </button>
          <button type="submit" onClick={() => deleteTodo(todo.id)}>
            削除
          </button>
        </li>
      ))}
    </ul>

    </>
  )
}

export default TodoList
