import { useState,useEffect } from "react";
import axios from "axios";
import "./App.css";
import {TodoHeader} from "./TodoHeader.js";
import { TaskButtonItem } from "./components/Atoms/TaskButtonItem";
import { TaskButtonList } from "./components/Atoms/TaskButtonList";


function App() {
  const [todos, setTodos] = useState([]);
	const [todoName, setTodoName] = useState("");

  const getTodos = () => {
    return axios
      .get("http://localhost:3000/todos")
      .then((res) => {
        if (res !== ''){
          setTodos(res.data);
        }
      })
      .catch(() => console.error);
  };


  const toggleComplete = async (id, index) => {
    const complete = todos[index].complete;
    await axios.put(`http://localhost:3000/todos/${id}`, {
      complete: !complete,
    });
    getTodos();
  };

  const createTodo = (e) => {

    if (todoName!==''){
     axios
     .post("http://localhost:3000/todos", {
       name: todoName,
       complete: false,
     })
     .then(() => {
       setTodoName("");
       getTodos();
     })
     .catch(() => console.error);
    }
    else{
     alert("入力欄が空です");
    }
     return;
 };

 const deleteTodo = (todoId) => {
  return axios
    .delete(`http://localhost:3000/todos/${todoId}`)
    .then(() => getTodos())
    .catch(console.error());
};

 const deleteAllTodo=()=>{
   const clearTodoList = [todos];
   const len =todos.length;
   let res=window.confirm("TODOリストを全て削除しますか？");

  if (res){
   for (let i = 0; i < len; i++){
    //axios.delete(`http://localhost:3000/todos/${todos[i].id}`)

    clearTodoList.splice(i, 1);
   }
   setTodos(clearTodoList);

   const sure = window.confirm('Are you sure?');
    if (sure) {
      axios.delete('/api/v1/todos/destroy_all')
      .then(resp => {
        setTodos([])
      })
      .catch(e => {
        console.log(e)
      })
    }


  }

 };


	useEffect(() => {
    getTodos();
  }, []);

  return (
    <>
    <div>
        <input
          type="text"
          value={todoName}
          onChange={(e) => setTodoName(e.target.value)}
        />
        <button type="submit" onClick={(e) => createTodo(e)}>
          追加
        </button>
        <button type="submit" onClick={() => deleteAllTodo()}>
          一括削除
        </button>
      </div>


		<ul>
		  {todos.map((todo, index) => (
		    <TaskButtonList index={index}>
          {todo.complete ? <s>{todo.name}</s> : todo.name}
					<TaskButtonItem  ButtonName="完了"  onClick={() => toggleComplete(todo.id, index)}/>
          <button type="submit" onClick={() => toggleComplete(todo.id, index)}>
          完了
          </button>

          <TaskButtonItem ButtonName="削除"  onClick={() => deleteTodo(todo.id)}/>
          <button type="submit" onClick={() => deleteTodo(todo.id)}>
          削除
          </button>

        </TaskButtonList>
		  ))}
		</ul>
    </>
	)
}

export default App;
