import React from 'react'
import { useState,useEffect } from "react";
import axios from "axios";
import styled from "styled-components";
import { Button } from '@mantine/core';

const [todoName, setTodoName] = useState("");

const getTodos = () => {
  return axios
    .get("http://localhost:3000/todos")
    .then((res) => {
      if (res !== ''){
        setTodos(res.data);
      }
    })
    .catch(() => console.error);
};

const createTodo = (e) => {
  if (todoName!==''){
   axios
   .post("http://localhost:3000/todos", {
     name: todoName,
     complete: false,
   })
   .then(() => {
     setTodoName("");
     getTodos();
   })
   .catch(() => console.error);
  }
  else{
   alert("入力欄が空です");
  }
   return;
};


export const TaskButtonItem = (props) => {
  const { ButtonName } = props;

  const Button_li = styled.li`
  list-style:none;
  padding-right:10px;
`;


  return (
  <>
    <input
          type="text"
          value={todoName}
          onChange={(e) => setTodoName(e.target.value)}
        />
    <Button_li>
      <Button size="xs" type="submit"　onClick={(e) => createTodo(e)}>{ButtonName}</Button>
    </Button_li>
  </>
  )
}
