import { useState,useEffect } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [todos, setTodos] = useState([]);
// useStateを追加
	const [todoName, setTodoName] = useState("");

  const getTodos = () => {
    return axios
      .get("http://localhost:3000/todos")

      .then((res) => {
        if (res !== ''){
          setTodos(res.data);
          console.log(res.data);
        }
      })


      .catch(() => console.error);
  };
// ここから追加
	const createTodo = (e) => {

     if (todoName!==''){
      axios
      .post("http://localhost:3000/todos", {
        name: todoName,
        complete: false,
      })
      .then(() => {
        setTodoName("");
        getTodos();
      })
      .catch(() => console.error);
     }
     else{
      alert("入力欄が空です");
     }

      return;
  };

  const deleteTodo = (todoId) => {
    return axios
      .delete(`http://localhost:3000/todos/${todoId}`)
      .then(() => getTodos())
      .catch(console.error());
  };

  const toggleComplete = async (id, index) => {
    const complete = todos[index].complete;
    await axios.put(`http://localhost:3000/todos/${id}`, {
      complete: !complete,
    });
    getTodos();
  };


  const AlldeleteTodo=()=>{
    const clearTodoList = [todos];
    const ids=[];
    console.log(clearTodoList);
    alert("TODOリストを全て削除しますか？");
    let len=clearTodoList.length;
    for (let i = 0; i < len; i++){
      //axios.delete(`http://localhost:3000/todos/${todos[i].id}`)
      ids.push(clearTodoList[i].id);
      clearTodoList.splice(i, 1);
    }
  setTodos(clearTodoList);

  console.log(ids);

  axios.delete(`http://localhost:3000/todos/${ids}`);
  };


	useEffect(() => {
    getTodos();
  }, []);

  return (
    <>
    <div>
      <input
        type="text"
        value={todoName}
        onChange={(e) => setTodoName(e.target.value)}
      />
      <button type="submit" onClick={(e) => createTodo(e)}>
        追加
      </button>
      <button type="submit" onClick={() => AlldeleteTodo()}>
        一括削除
      </button>
    </div>
		<ul>
		  {todos.map((todo, index) => (
		    <li index={index}>
          {todo.complete ? <s>{todo.name}</s> : todo.name}
					<button type="submit" onClick={() => toggleComplete(todo.id, index)}>
            完了
          </button>
          <button type="submit" onClick={() => deleteTodo(todo.id)}>
            削除
          </button>
          </li>
		  ))}
		</ul>
    </>
	)
}

export default App;
