import React, { useState, useEffect } from 'react'
import axios from 'axios'
import styled from 'styled-components'
import { Checkbox } from '@mantine/core';
import { CloseButton, Group } from '@mantine/core';


const Task_li  =styled.li`
list-style:none;
font-size:25px;
display:flex;
`;

const TaskName_div = styled.div`
  width:250px;
`;

const TaskName = styled.p`
 padding-right:20px;
`;


const Button_li = styled.li`
  list-style:none;
  padding-top:20px;
`;

const Checkbox_div = styled.div`
  padding-top:13px;
`;



function TodoList() {
  const [todos, setTodos] = useState([]);

  const getTodos = () => {
    return axios
      .get("http://localhost:3000/todos")
      .then((res) => {
        if (res !== ''){
          setTodos(res.data);
        }
      })
      .catch(() => console.error);
  };

  const toggleComplete = async (id, index) => {
    const complete = todos[index].complete;
    await axios.put(`http://localhost:3000/todos/${id}`, {
      complete: !complete,
    });
    getTodos();
  };

  const deleteTodo = (todoId,index) => {
    const complete = todos[index].complete;
    axios
    .put(`http://localhost:3000/todos/${todoId}`, {
      complete: !complete
    });
    getTodos()

    axios
      .delete(`http://localhost:3000/todos/${todoId}`)
      .then(() => 
      getTodos()
      )
      .catch(console.error());

  };

  useEffect(() => {
    getTodos();
  }, []);



  return (
    <>
      <h1>Your Todo List</h1>
      <ul>
      {todos.map((todo, index) => (
        <Task_li index={index}>
          <TaskName_div> 
            <TaskName>{todo.complete ? <s>{todo.name}</s>: todo.name}
            </TaskName>
          </TaskName_div>

          <Button_li>
            <Checkbox_div>
            <Checkbox  onClick={() => toggleComplete(todo.id, index)}/>
            </Checkbox_div>
          </Button_li>

          <Button_li>
            <CloseButton  onClick={() => deleteTodo(todo.id, index)} title="Close popover" size="xl" iconSize={15} />
          </Button_li>

        </Task_li>
      ))}
    </ul>

    </>
  )
}

export default TodoList
