import { useState,useEffect } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [todos, setTodos] = useState([]);
// useStateを追加
	const [todoName, setTodoName] = useState("");

  const getTodos = () => {
    return axios
      .get("http://localhost:3010/todos")
      .then((res) => {
        setTodos(res.data);
        console.log(res.data);
      })
      .catch(() => console.error);
  };
// ここから追加
	const createTodo = () => {
    return axios
      .post("http://localhost:3010/todos", {
        name: todoName,
        complete: false,
      })
      .then(() => {
        setTodoName("");
        getTodos();
      })
      .catch(() => console.error());
  };
//ここまで追加

	useEffect(() => {
    getTodos();
  }, []);


}

export default App;
