import { useState,useEffect } from "react";
import axios from "axios";
import "./App.css";
import styled from 'styled-components'

const Nabvar = styled.nav`
  background: #dbfffe;
  min-height: 8vh;
  display: flex;
  justify-content: space-around;
  align-items: center;
`

const Logo = styled.div`
  font-weight: bold;
  font-size: 23px;
  letter-spacing: 3px;
`

const NavItems = styled.ul`
  display: flex;
  width: 400px;
  max-width: 40%;
  justify-content: space-around;
  list-style: none;
`

const NavItem = styled.li`
  font-size: 19px;
  font-weight: bold;
  opacity: 0.7;
  &:hover {
    opacity: 1;
  }
`

const Wrapper = styled.div`
  width: 700px;
  max-width: 85%;
  margin: 20px auto;
`

function App() {
  const [todos, setTodos] = useState([]);
	const [todoName, setTodoName] = useState("");

  const getTodos = () => {
    return axios
      .get("http://localhost:3000/todos")
      .then((res) => {
        if (res !== ''){
          setTodos(res.data);
        }
      })
      .catch(() => console.error);
  };


  const toggleComplete = async (id, index) => {
    const complete = todos[index].complete;
    await axios.put(`http://localhost:3000/todos/${id}`, {
      complete: !complete,
    });
    getTodos();
  };

  const createTodo = (e) => {

    if (todoName!==''){
     axios
     .post("http://localhost:3000/todos", {
       name: todoName,
       complete: false,
     })
     .then(() => {
       setTodoName("");
       getTodos();
     })
     .catch(() => console.error);
    }
    else{
     alert("入力欄が空です");
    }
     return;
 };

 const deleteTodo = (todoId) => {
  return axios
    .delete(`http://localhost:3000/todos/${todoId}`)
    .then(() => getTodos())
    .catch(console.error());
};

 const deleteAllTodo=()=>{
   const clearTodoList = [todos];
   const len =todos.length;
   let res=window.confirm("TODOリストを全て削除しますか？");

  if (res){
   for (let i = 0; i < len; i++){
    //axios.delete(`http://localhost:3000/todos/${todos[i].id}`)

    clearTodoList.splice(i, 1);
   }
   setTodos(clearTodoList);

    if (res) {
      axios.delete('http://localhost:3000/todos/destroy_all')
      .then(()=> {
        setTodos([])
      })
      .catch(e => {
        console.log(e)
      })
    }


  }

 };


	useEffect(() => {
    getTodos();
  }, []);

  return (
