import React, { useState, useEffect } from 'react'
import axios from 'axios'
import styled from 'styled-components'
//import { Button } from '@mantine/core';
import { TextInput } from '@mantine/core';
import { useDisclosure, useCounter } from '@mantine/hooks';
import { Modal, Button, Group, Text, Badge } from '@mantine/core';


function AddTodo() {
  const [todos, setTodos] = useState([]);
  const [todoName, setTodoName] = useState("");

  const [opened, { close, open }] = useDisclosure(false);
  const [count, { increment, decrement }] = useCounter(3, { min: 0 });

  const badges = Array(count)
  .fill(0)
  .map((_, index) => <Badge key={index}>Badge {index}</Badge>);

  const Input_Button_div  =styled.div`
  // display:flex;
`;


  const Input_div  =styled.div`
  width:60%;
`;

  const Button_div  =styled.div`

`;

  const getTodos = () => {
    return axios
      .get("http://localhost:3000/todos")
      .then((res) => {
        if (res !== ''){
          setTodos(res.data);
        }
      })
      .catch(() => console.error);
  };

  const createTodo = (e) => {

    if (todoName!==''){
     axios
     .post("http://localhost:3000/todos", {
       name: todoName,
       complete: false,
     })
     .then(() => {
       setTodoName("");
       getTodos();
     })
     .catch(() => console.error);

     setTodos([...todos, { todo: todoName, complete:false }]);
     getTodos();
    }
    else{
     alert("入力欄が空です");
    }
    return;
 };

const deleteAllTodo=()=>{
   let res=window.confirm("TODOリストを全て削除しますか？");
    if (res) {
      axios.delete(`http://localhost:3000/todos/destroy_all`)
      .then(()=> {
        setTodos([])
      })
      .catch(e => {
        console.log(e)
     })
  }
};


useEffect(() => {
  getTodos();
}, []);


  return (
    <>

    <Modal opened={opened} onClose={close} size="auto">
        <Text>Delete only done task? or all task?</Text>

        <Group mt="xl">
          <Button variant="outline" onClick={increment}>
            Clear Done Task
          </Button>
          <Button variant="outline" onClick={decrement}>
            Delete All Task
          </Button>
        </Group>

    </Modal>
    <Group position="center">
      <Button onClick={open}>Open modal</Button>
    </Group>

      <h1>Add New Todo</h1>
      <>
        {/* <Input_div> */}
          <TextInput
            placeholder="Task name"
            type="text"
            value={todoName}
            onChange={(e) => setTodoName(e.target.value)}
          />
        {/* </Input_div> */}

      <Button_div >
       <Button type="submit" onClick={(e) => createTodo(e)}>
          ADD
        </Button>
        <Button type="submit"  color="red"  onClick={() => deleteAllTodo()}>
          Delete
        </Button>
      </Button_div>

      </>
    </>
  )
}

export default AddTodo
