import React, { useState, useEffect } from 'react'
import axios from 'axios'
import styled from 'styled-components'
import { Button } from '@mantine/core';
import { TextInput } from '@mantine/core';


function AddTodo() {
  const [todos, setTodos] = useState([]);
  const [todoName, setTodoName] = useState("");

  const getTodos = () => {
    return axios
      .get("http://localhost:3000/todos")
      .then((res) => {
        if (res !== ''){
          setTodos(res.data);
        }
      })
      .catch(() => console.error);
  };

  const createTodo = (e) => {

    if (todoName!==''){
     axios
     .post("http://localhost:3000/todos", {
       name: todoName,
       complete: false,
     })
     .then(() => {
       setTodoName("");
       getTodos();
     })
     .catch(() => console.error);
    }
    else{
     alert("入力欄が空です");
    }
     return;
 };

const deleteAllTodo=()=>{
  const clearTodoList = [todos];
  const len =todos.length;
  let res=window.confirm("TODOリストを全て削除しますか？");

 if (res){
  for (let i = 0; i < len; i++){
    console.log(todos[i].id);
   axios.delete(`http://localhost:3000/todos/${todos[i].id}`)
   .then(() => { })
  .catch(() => console.error);
   clearTodoList.splice(i, 1);
  }

  //setTodos(clearTodoList);
  setTodos([]);
 }

};


useEffect(() => {
  getTodos();
}, []);


  return (
    <>
      <h1>Add New Todo</h1>
      <div>
        <input
          type="text"
          value={todoName}
          onChange={(e) => setTodoName(e.target.value)}
        />
        <Button type="submit" onClick={(e) => createTodo(e)}>
          ADD
        </Button>
        <Button type="submit"  color="red"  onClick={() => deleteAllTodo()}>
          Delete All
        </Button>
      </div>
    </>
  )
}

export default AddTodo
