// useStateを追加
const [todoName, setTodoName] = useState("");

const createTodo = (e) => {
  if (todoName!==''){
   axios
   .post("http://localhost:3000/todos", {
     name: todoName,
     complete: false,
   })
   .then(() => {
     setTodoName("");
     getTodos();
   })
   .catch(() => console.error);
  }
  else{
   alert("入力欄が空です");
  }
   return;
};


export const TaskButtonItem = (props) => {
  const { ButtonName } = props;

  const Button_li = styled.li`
  list-style:none;
  padding-right:10px;
`;


  return (
    <Button_li>
      <Button size="xs" type="submit">{ButtonName}</Button>
    </Button_li>

  )
}
