import { useState,useEffect } from "react";
import axios from "axios";
import "./App.css";
import {TodoHeader} from "./TodoHeader.js";
import { TaskButtonItem } from "./components/Atoms/TaskButtonItem";
import { TaskButtonList } from "./components/Atoms/TaskButtonList";

function App() {
  const [todos, setTodos] = useState([]);
// useStateを追加
	const [todoName, setTodoName] = useState("");

  const getTodos = () => {
    return axios
      .get("http://localhost:3000/todos")
      .then((res) => {
        if (res !== ''){
          setTodos(res.data);
        }
      })


      .catch(() => console.error);
  };
// ここから追加
	const createTodo = (e) => {

     if (todoName!==''){
      axios
      .post("http://localhost:3000/todos", {
        name: todoName,
        complete: false,
      })
      .then(() => {
        setTodoName("");
        getTodos();
      })
      .catch(() => console.error);
     }
     else{
      alert("入力欄が空です");
     }

      return;
  };

  const deleteTodo = (todoId) => {
    return axios
      .delete(`http://localhost:3000/todos/${todoId}`)
      .then(() => getTodos())
      .catch(console.error());
  };

  const toggleComplete = async (id, index) => {
    const complete = todos[index].complete;
    await axios.put(`http://localhost:3000/todos/${id}`, {
      complete: !complete,
    });
    getTodos();
  };


  const AlldeleteTodo=()=>{
    const clearTodoList = [todos];
    const len =todos.length;
    let res=window.confirm("TODOリストを全て削除しますか？");

   if (res){
    for (let i = 0; i < len; i++){
     axios.delete(`http://localhost:3000/todos/${todos[i].id}`)

     clearTodoList.splice(i, 1);
    }
    setTodos(clearTodoList);
   }

   //axios.delete(`http://localhost:3000/todos`)
  };

  const onClickTest=() =>{
    alert("hello");

  };


	useEffect(() => {
    getTodos();
  }, []);

  return (
    <>
    {TodoHeader}
		<ul>
		  {todos.map((todo, index) => (
		    <TaskButtonList index={index}>
          {todo.complete ? <s>{todo.name}</s> : todo.name}
					<TaskButtonItem  ButtonName="完了"  onClick={() => toggleComplete(todo.id, index)}/>

          <TaskButtonItem ButtonName="削除"  onClick={() => deleteTodo(todo.id)}/>

        </TaskButtonList>
		  ))}
		</ul>
    </>
	)
}

export default App;
