import { useState,useEffect } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [todos, setTodos] = useState([]);

  const getTodos = () => {
    return axios
      .get("http://localhost:3000/todos")
      .then((res) => {
        setTodos(res.data);
        console.log(res.data);
      })
      .catch(() => console.error);
  };
	useEffect(() => {
    getTodos();
  }, []);

  return (
		<ul>
		  {todos.map((todo, index) => (
		    <li index={index}>{todo.name}</li>
		  ))}
		</ul>
	)
}

export default App;
