import { useState,useEffect } from "react";
import axios from "axios";
import './css/App.css';
import styled from 'styled-components'
import AddTodo from './AddTodo'
import TodoList from './TodoList'


const Nabvar = styled.nav`
  background: #dbfffe;
  min-height: 8vh;
  display: flex;
  justify-content: space-around;
  align-items: center;
`

const Logo = styled.div`
  font-weight: bold;
  font-size: 23px;
  letter-spacing: 3px;
`

const NavItems = styled.ul`
  display: flex;
  width: 400px;
  max-width: 40%;
  justify-content: space-around;
  list-style: none;
`

const NavItem = styled.li`
  font-size: 19px;
  font-weight: bold;
  opacity: 0.7;
  &:hover {
    opacity: 1;
  }
`

const Wrapper = styled.div`
  width: 700px;
  max-width: 85%;
  margin: 20px auto;
`

function App() {
  const [todos, setTodos] = useState([]);

 const deleteAllTodo=()=>{
   const clearTodoList = [todos];
   const len =todos.length;
   let res=window.confirm("TODOリストを全て削除しますか？");

  if (res){
   for (let i = 0; i < len; i++){
    axios
    .delete(`http://localhost:3000/todos/${todos[i].id}`)
    .then (()=>console.log("noproblem"))
    .catch(() => console.error);

    clearTodoList.splice(i, 1);

   }
   setTodos([]);
  }

 };

  return (
    <>
  <button type="submit" onClick={() => deleteAllTodo()}>
          一括削除
        </button>
  <Wrapper>

      <AddTodo /> 

       <TodoList /> 

    </Wrapper>
    </>
	)
}

export default App;
